mapper.select:
SELECT id, name 
  FROM investigator_info
 WHERE 1=1

   AND grade != #{negrade}


   AND grade == #{grade}

mapper.insert:
INSERT IGNORE INTO case_user_auth (
       id
     , case_id
) VALUES (
       #{id}
     , #{case_id}
)

mapper.delete:
DELETE FROM case_user_auth
 WHERE 1=1
   AND id = #{id}
   AND case_id = #{case_id}
