mapper.select:
SELECT a.file_id as id ,a.name ,a.par_id FROM file_info a ,partition_info b WHERE a.parent_id = #{id} AND b.evd_id = #{evd_id}
AND b.par_id = #{par_id}
AND a.par_id = b.par_id AND a.dir_type = 3 ORDER BY a.name

SELECT a.file_id AS id, a.name, a.dir_type, a.size, a.md5, a.extension, a.id AS seq , FROM_UNIXTIME(a.ctime, '%Y-%m-%d %H:%i:%s') AS ctime , a.ctime_nano AS ctime_nano , FROM_UNIXTIME(a.atime, '%Y-%m-%d %H:%i:%s') AS atime , a.atime_nano AS atime_nano , FROM_UNIXTIME(a.mtime, '%Y-%m-%d %H:%i:%s') AS mtime , a.mtime_nano AS mtime_nano , FROM_UNIXTIME(a.etime, '%Y-%m-%d %H:%i:%s') AS etime , a.etime_nano AS etime_nano FROM file_info a, partition_info b WHERE 1=1
AND a.parent_id = #{id}
AND b.evd_id = #{evd_id} AND (a.dir_type = 3 OR a.dir_type = 5)
AND b.par_id = #{par_id}
AND a.par_id = b.par_id
AND a.name LIKE CONCAT(#{search_fname}, '%')
AND a.size >= #{search_ssize}
AND a.size <= #{search_esize}
AND a.mtime >= UNIX_TIMESTAMP(#{search_stime})
AND a.mtime <= UNIX_TIMESTAMP(#{search_etime})
AND a.ctime >= UNIX_TIMESTAMP(#{search_stime})
AND a.ctime <= UNIX_TIMESTAMP(#{search_etime})
AND a.atime >= UNIX_TIMESTAMP(#{search_stime})
AND a.atime <= UNIX_TIMESTAMP(#{search_etime})
ORDER BY a.dir_type, a.name
LIMIT #{offset}, #{pageSize}

SELECT name, parent_path, extension
  FROM file_info
 WHERE 1=1
   AND id = #{id}
   AND file_id = #{file_id}
