mapper.select:
SELECT A.timestamp , A.regdate , A.package_name , A.contents , A.latitude , A.longitude FROM ( SELECT DATE_FORMAT(STR_TO_DATE(timestamp, '%Y-%m-%dT%H:%i:%s'), '%Y-%m-%d %H:%i:%s') AS timestamp , STR_TO_DATE(timestamp, '%Y-%m-%dT%H:%i:%s') AS regdate , package_name , contents , latitude , longitude FROM lv1_os_and_geodata WHERE 1=1 AND case_id = #{case_id} ) A WHERE 1=1 AND A.regdate >= #{sdate}
AND A.regdate <= #{edate}
AND TIME(A.regdate) >= #{stime}
AND TIME(A.regdate) <= #{etime}
AND (TIME(A.regdate) >= #{stime} OR TIME(A.regdate) <= #{etime})
AND (DAYOFWEEK(A.regdate) = 1 OR DAYOFWEEK(A.regdate) = 7)
AND (DAYOFWEEK(A.regdate) != 1 AND DAYOFWEEK(A.regdate) != 7)
ORDER BY A.regdate ASC

SELECT timestamp, COUNT(timestamp) AS cnt
  FROM (
        SELECT DATE_FORMAT(STR_TO_DATE(timestamp, '%Y-%m-%dT%H:%i:%s'), '%Y-%m-%d %H:%i:%s') AS timestamp
          FROM lv1_os_and_geodata
         WHERE 1=1
           AND case_id = #{case_id}
           AND STR_TO_DATE(timestamp, '%Y-%m-%dT%H:%i:%s') >= #{sdate} 

           AND STR_TO_DATE(timestamp, '%Y-%m-%dT%H:%i:%s') <= #{edate}

       ) A 
 GROUP BY A.timestamp;

SELECT DATE_FORMAT(regdate, '%Y-%m-%d %H:%i:%s') AS regdate
     , type
     , name 
  FROM gps_file
 WHERE 1=1
   AND case_id = #{case_id}
   AND regdate >= #{sdate}

   AND regdate <= #{edate}

 ORDER BY regdate ASC


SELECT roomno, GROUP_CONCAT(DISTINCT name) AS name, content, DATE_FORMAT(regdate, '%Y-%m-%d %H:%i:%s') AS regdate
 FROM (
       SELECT roomno, name, content, regdate
         FROM (
               SELECT roomno, sender_name AS name, content, regdate
                 FROM communication_data
                WHERE 1=1

                  AND regdate >= #{sdate}



                  AND regdate <= #{edate}


                  AND roomno IS NOT NULL
                  AND case_id = #{case_id}
                UNION
               SELECT roomno, receiver_name AS name, content, regdate
                 FROM communication_data
                WHERE 1=1

                  AND regdate >= #{sdate}



                  AND regdate <= #{edate}

                  AND roomno IS NOT NULL
                  AND case_id = #{case_id}

              ) AA
        ORDER BY regdate DESC
        LIMIT 18446744073709551615
      ) A
WHERE 1=1
  AND name != ''
GROUP BY roomno


SELECT seqno, roomno, app, content, sender, sender_name, receiver
     , receiver_name, msg_type, isdelete, DATE_FORMAT(regdate,'%Y-%m-%d %H:%i:%s') AS regdate
     , case_id
  FROM communication_data 
 WHERE 1=1
   AND case_id = #{case_id}
   AND roomno = #{roomno}
 ORDER BY regdate DESC

 LIMIT ${sdata}, ${pageCnt}
